import React from "react";
import Fade from "react-reveal/Fade";

const MyProjects = props => {
  // Title, description, img , fade direction
  const projets = [
    {
      title: "Funny Shop",
      description:
        " Site de E commerce 'bac à sable' créé pour parfaire mes compétences et principalement avec le framework Symfony.",
      descriptionSub:
        " Mise en place de mail automatique lors de l'inscription, interface d'administration (CRUD), panier d'achat actuellement en constante évolution !",
      fade: "left",
      img:
        "https://images.pexels.com/photos/226664/pexels-photo-226664.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"
    }
  ];

  return (
    <>
      <div className="myProjects">
        {projets.map(projet => (
          <div className="row justify-content-center hideSm">
            <Fade duration={3000} direction="left">
              <div className="col-4">
                <img alt="" src={projet.img} className="fadeImg" />
              </div>
              <div className="col col-4 card p-5">
                <h3 className="text-center lora p-4 underline">
                  {projet.title}
                </h3>
                <p className="text-center sourceSans pb-3 fs-2 opacity-semi">
                  Je peux transmettre ma passion
                </p>
                <p className="text-justify concertone">{projet.description}</p>
                <p className="text-justify concertone">
                  {projet.descriptionSub}
                </p>
                <div className="d-flex justify-content-center">
                  <button type="button" class="btn btn-primary">
                    Symfony
                  </button>
                  <button type="button" class="btn btn-primary">
                    Symfony
                  </button>
                  <button type="button" class="btn btn-primary">
                    Symfony
                  </button>
                </div>
              </div>

              <div className="bandeau"></div>
            </Fade>
          </div>
        ))}
      </div>
    </>
  );
};

export default MyProjects;
