import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slide from "react-reveal/Slide";
import Fade from "react-reveal/Fade";
import Loader from "react-loader-spinner";
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";

const Carousel = props => {
  const [loading, setLoading] = useState(true);
  const myRef = React.createRef();

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    fade: true,
    afterChange: event => {
      console.log(event);
    }
  };

  useEffect(() => {
    setTimeout(function() {
      //Start the timer
      setLoading(false); //After 1 second, set render to true
    }, 1000);
  });

  return (
    <>
      {(loading && (
        <div className="loader" onLoad={() => console.log("LOADED")}>
          <Loader
            type="Triangle"
            color="#3f3333"
            height={200}
            width={200}
            timeout={1000} //3 secs
          />
        </div>
      )) || (
        <Fade>
          <Slider {...settings}>
            {/* Slide 1 */}
            <div ref={myRef} className="carouselSlide  myCyan">
              <Slide top>
                <h3 className="myTitle">Bonjour, je suis Noël</h3>
              </Slide>
              <Fade right>
                <p className="underTitle">Je suis développeur web</p>
              </Fade>
            </div>
            {/* End Slide 1 */}
            {/* Slide 2 */}
            <div className="carouselSlide2 hide">
              <Slide top>
                <h3 className="myTitleAboutMe">About me</h3>
              </Slide>
              <Fade right>
                <div className="textAboutMe test">
                  <p className="underTitle1">
                    Bonjour je me présente Noël AN. Après avoir travaillé
                    principalement dans la restauration et la logistique, j'ai
                    décidé de me reconvertir dans un domaine qui m'a toujours
                    passionné, le devéloppement Web.
                  </p>
                  <p className="underTitle2">
                    J'ai effectué une formation de développeur web à la Wild
                    Code School qui m'a permis de découvrir ce métier et me
                    spécialiser en PHP Symfony.
                  </p>
                  <p className="underTitle3">
                    Je continu de m'auto-former continuellement, actuellement
                    j'ai pu découvrir React et Api platform
                  </p>
                  <p className="underTitle4">
                    Ayant terminé un stage en application mobile pour Senzu je
                    suis actuellement à lécoute d'oportunité !
                  </p>
                </div>
              </Fade>
            </div>
            {/* End Slide 2 */}

            <div className="carouselSlide solid myGrey">
              <h3>1</h3>
            </div>
            <div className="carouselSlide solid myRed">
              <h3>2</h3>
            </div>
            <div className="carouselSlide solid myCyan">
              <h3>1</h3>
            </div>
            <div className="carouselSlide solid myRed">
              <h3>2</h3>
            </div>
          </Slider>
        </Fade>
      )}
    </>
  );
};

export default Carousel;
